DROP TABLE tech_layoffs;
CREATE TABLE tech_layoffs(
   company                       VARCHAR(22) NOT NULL
  ,total_layoffs                 INTEGER  NOT NULL
  ,impacted_workforce_percentage VARCHAR(7) NOT NULL
  ,reported_date                 DATE  NOT NULL
  ,industry                      VARCHAR(14) NOT NULL
  ,headquarter_location          VARCHAR(14) NOT NULL
  ,sources                       VARCHAR(40) NOT NULL
  ,status                        VARCHAR(7) NOT NULL
  ,additional_notes              VARCHAR(179)
);
